import classes from "./ForSale.module.css";
import products from "./Products";

let ForSale = () => {
  let innerHTML = "";
  console.log("hello");
  for (let i = 0; i < products.length; i++) {
    let innerHTMLloop = `
              <div class="card">
                  <div class="images">
                  <h2 class="product-name">${products[i].name}</h2>
                      <img class="large-photo" src="${products[i].img_url1}" alt="${products[i].name}">
                      <div class="small-photo">
                          <img class="product_sm" src="${products[i].img_url2}" alt="${products[i].name}">
