import React, { useState } from "react";
import classes from "./Contact.module.css";

const Form = () => {
  const [firstName, setFirstName] = useState("");
  const [enteredNameIsValid, setEnteredNameIsValid] = useState(false);
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");

  const setFirstNameHandler = (e) => {
    setFirstName(e.target.value);
    console.log(firstName);
  };

  const setLastNameHandler = (e) => {
    setLastName(e.target.value);
    console.log(lastName);
  };

  const setEmailHandler = (e) => {
    setEmail(e.target.value);
    console.log(email);
  };

  const setMessageHandler = (e) => {
    setMessage(e.target.value);
    console.log(message);
  };

  const submitFormHandler = (e) => {
    e.preventDefault();

    if (firstName.trim() === "") {
      return console.log("error");
    }

    console.log("form submit pressed");
    setFirstName("");
    setLastName("");
    setEmail("");
    setMessage("");
  };

  return (
    <form onSubmit={submitFormHandler}>
      <div className={classes["control-group"]}>
        <div className={classes["form-control"]}>
          <label htmlFor="name">First Name</label>
          <input
            id="name"
            type="text"
            onChange={setFirstNameHandler}
            value={firstName}
            required
          />
          {enteredNameIsValid && <p>Error</p>}
        </div>
        <div className={classes["form-control"]}>
          <label htmlFor="name">Last Name</label>
          <input
            id="name"
            type="text"
            onChange={setLastNameHandler}
            value={lastName}
            required
          />
        </div>
        <div className={classes["form-control"]}>
          <label htmlFor="name">Email</label>
          <input
            id="name"
            type="text"
            onChange={setEmailHandler}
            value={email}
            required
          />
        </div>
        <div className={classes["form-control"]}>
          <label htmlFor="name">Comment</label>
          <textarea
            id="name"
            type="comment"
            onChange={setMessageHandler}
            value={message}
            required
            rows="5"
          />
        </div>
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

const Contact = () => {
  const [theState, setState] = useState(false);

  return (
    <div className={`${classes["contact"]} ${"container"}`} id="contact">
      <h1>Contact</h1>
      <h3>Or</h3>
      {!theState && (
        <button
          onClick={() => {
            setState(!theState);
          }}
        >
          Show Form
        </button>
      )}
      {theState && <Form />}
    </div>
  );
};

export default Contact;
