import classes from "./Contact.module.css";
import { useState } from "react";

const Contact = () => {
  const [firstNameInput, setFirstName] = useState();
  const [lastNameInput, setLastName] = useState();
  const [emailInput, setEmail] = useState();
  const [messageInput, setMessage] = useState();
  const [theState, changeState] = useState(false);

  const setNameHandler = (event) => {
    setFirstName(event.target.value);
  };

  const setLastNameHandler = (event) => {
    setLastName(event.target.value);
  };

  const setEmailHandler = (event) => {
    setEmail(event.target.value);
  };

  const setMessageHandler = (event) => {
    setMessage(event.target.value);
  };

  const submitFormHandler = (event) => {
    event.preventDefault();

    console.log(firstNameInput);
    console.log(lastNameInput);
    console.log(emailInput);
    console.log(messageInput);

    // firstNameInputRef.current.value = "";
    // lastNameInputRef.current.value = "";
    // emailInputRef.current.value = "";
    // messageInputRef.current.value = "";
  };

  const Email = () => {
    return (
      <a href="mailto:jogucareers@gmail.com">
        <h3 className={`${classes["email"]} margin-center`}>Email</h3>
      </a>
    );
  };

  const Form = () => {
    return (
      <form onSubmit={submitFormHandler}>
        <div className={classes["control-group"]}>
          <div className={classes["form-control"]}>
            <label htmlFor="name">First Name</label>
            <input id="name" type="text" onChange={setNameHandler} />
          </div>
          <div className={classes["form-control"]}>
            <label htmlFor="name">Last Name</label>
            <input id="name" type="text" onChange={setLastNameHandler} />
          </div>
        </div>
        <div className={classes["form-control"]}>
          <label htmlFor="name">E-Mail Address</label>
          <input id="name" type="text" onChange={setEmailHandler} />
        </div>
        <div className={classes["form-control"]}>
          <label htmlFor="name">Message</label>
          <textarea
            id="name"
            type="note"
            onChange={setMessageHandler}
            rows="8"
          />
        </div>
        <div className="form-actions">
          <button>Submit</button>
        </div>
      </form>
    );
  };

  return (
    <div className={`${classes["contact"]} ${"container"}`} id="contact">
      <h1>Contact</h1>
      <Email />
      <h3>Or</h3>
      {!theState && (
        <button
          onClick={() => {
            changeState(!theState);
          }}
        >
          Show Form
        </button>
      )}
      {theState && <Form />}
    </div>
  );
};

export default Contact;
