import classes from "./Contact.module.css";
import { useState } from "react";

const Contact = () => {
  const [firstNameInput, setFirstName] = useState("");
  const [lastNameInput, setLastName] = useState("");
  const [emailInput, setEmail] = useState("");
  const [messageInput, setMessage] = useState("");
  const [theState, changeState] = useState(false);

  const setFirstNameHandler = (e) => {
    setFirstName(e.target.value);
  };

  const setLastNameHandler = (e) => {
    setLastName(e.target.value);
  };

  // const setEmailHandler = (e) => {
  //   setEmail(e.target.value);
  // };

  const setMessageHandler = (e) => {
    setMessage(e.target.value);
  };

  const submitFormHandler = (e) => {
    e.preventDefault();

    console.log(firstNameInput);
    console.log(lastNameInput);
    console.log(emailInput);
    console.log(messageInput);

    setFirstName("");
    setLastName("");
    // setEmailHandler("");
    setMessageHandler("");
  };

  const Email = () => {
    return (
      <a href="mailto:jogucareers@gmail.com">
        <h3 className={`${classes["email"]} margin-center`}>Email</h3>
      </a>
    );
  };

  const Form = () => {
    return (
      <form onSubmit={submitFormHandler}>
        <div className={classes["control-group"]}>
          <div className={classes["form-control"]}>
            <label htmlFor="name">First Name</label>
            <input
              id="name"
              type="text"
              onChange={setFirstNameHandler}
              value={firstNameInput}
            />
          </div>
          <div className={classes["form-control"]}>
            <label htmlFor="name">Last Name</label>
            <input
              id="name"
              type="text"
              onChange={setLastNameHandler}
              value={lastNameInput}
            />
          </div>
        </div>
        <div className={classes["form-control"]}>
          <label htmlFor="name">E-Mail Address</label>
          <input
            id="name"
            type="text"
            // onChange={setEmailHandler}
            value={emailInput}
          />
        </div>
        <div className={classes["form-control"]}>
          <label htmlFor="name">Message</label>
          <textarea
            id="name"
            type="note"
            onChange={setMessageHandler}
            value={messageInput}
            rows="8"
          />
        </div>
        <div className="form-actions">
          <button>Submit</button>
        </div>
      </form>
    );
  };

  return (
    <div className={`${classes["contact"]} ${"container"}`} id="contact">
      <h1>Contact</h1>
      <Email />
      <h3>Or</h3>
      {!theState && (
        <button
          onClick={() => {
            changeState(!theState);
          }}
        >
          Show Form
        </button>
      )}
      {theState && <Form />}
    </div>
  );
};

export default Contact;
