import classes from "./Contact.module.css";
import { useRef, useState } from "react";

const Contact = () => {
  const [state, changeState] = useState(false);
  const nameInputRef = useRef();
  const [enteredFirstName, setEnteredName] = useState("");
  const [enteredLastName, setEnteredLastName] = useState("");
  const [enteredEmail, setEnteredEmail] = useState("");
  const [enteredMessage, setEnteredMessage] = useState("");

  const submitFormHandler = (event) => {
    event.preventDefault();
  };

  const nameInputChangeHandler = (event) => {
    setEnteredName(event.target.value);
    console.log(enteredFirstName);
  };
  const lastNameInputChangeHandler = (event) => {
    setEnteredLastName(event.target.value);
    console.log(enteredLastName);
  };

  const emailInputChangeHandler = (event) => {
    setEnteredEmail(event.target.value);
    console.log(enteredEmail);
  };

  const messageInputChangeHandler = (event) => {
    setEnteredMessage(event.target.value);
    console.log(enteredMessage);
  };

  const Email = () => {
    return (
      <a href="mailto:jogucareers@gmail.com">
        <h3 className={`${classes["email"]} margin-center`}>Email</h3>
      </a>
    );
  };

  const Form = () => {
    return (
      <form onSubmit={submitFormHandler}>
        <div className={classes["control-group"]}>
          <div className={classes["form-control"]}>
            <label htmlFor="name">First Name</label>
            <input type="text" id="name" onSubmit={nameInputChangeHandler} />
          </div>
          <div className={classes["form-control"]}>
            <label htmlFor="name">Last Name</label>
            <input
              type="text"
              id="name"
              onChange={lastNameInputChangeHandler}
            />
          </div>
        </div>
        <div className={classes["form-control"]}>
          <label htmlFor="name">E-Mail Address</label>
          <input type="text" id="name" onChange={emailInputChangeHandler} />
        </div>
        <div className={classes["form-control"]}>
          <label htmlFor="name">Message</label>
          <textarea
            type="note"
            id="name"
            rows="8"
            onChange={messageInputChangeHandler}
          />
        </div>
        <div className="form-actions">
          <button>Submit</button>
        </div>
      </form>
    );
  };

  return (
    <div className={`${classes["contact"]} ${"container"}`} id="contact">
      <h1>Contact</h1>
      <Email />
      <h3>Or</h3>
      {!state && (
        <button
          onClick={() => {
            changeState(!state);
          }}
        >
          Show Form
        </button>
      )}
      {state && <Form />}
    </div>
  );
};

export default Contact;
