import React, { useState } from "react";
import classes from "./Contact.module.css";

const Form = () => {
  const [values, setValues] = useState({
    firstName: "",
    lastName: "",
    email: "",
    message: "",
  });

  const [submitted, setSubmitted] = useState(false);

  const setFirstNameHandler = (e) => {
    setValues({ ...values, firstName: e.target.value });
  };

  const setLastNameHandler = (e) => {
    setValues({ ...values, lastName: e.target.value });
  };

  const setEmailHandler = (e) => {
    setValues({ ...values, email: e.target.value });
  };

  const setMessageHandler = (e) => {
    setValues({ ...values, message: e.target.value });
  };

  const submitFormHandler = (e) => {
    e.preventDefault();
  };

  return (
    <div className={classes.container}>
      <form className={classes["control-group"]} onSubmit={submitFormHandler}>
        {submitted ? (
          <div className={classes.success_message}>
            Form has been submitted!
          </div>
        ) : null}
        <div className={classes["form-control"]}>
          <label htmlFor="name">First Name</label>
          <input
            id="name"
            type="text"
            name="fist-name"
            onChange={setFirstNameHandler}
            placeholder="First Name"
            value={values.firstName}
          />
        </div>
        <div className={classes["form-control"]}>
          <label htmlFor="name">Last Name</label>
          <input
            id="name"
            type="text"
            name="last-name"
            onChange={setLastNameHandler}
            placeholder="Last Name"
            value={values.lastName}
          />
        </div>
        <div className={classes["form-control"]}>
          <label htmlFor="name">Email</label>
          <input
            id="name"
            type="email"
            name="email"
            onChange={setEmailHandler}
            placeholder="Email"
            value={values.email}
          />
        </div>
        <div className={classes["form-control"]}>
          <label htmlFor="name">Message</label>
          <textarea
            id="name"
            type="comment"
            name="message"
            onChange={setMessageHandler}
            placeholder="Message"
            value={values.message}
            rows="5"
          />
        </div>
      </form>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </div>
  );
};

const Contact = () => {
  const [theState, setState] = useState(false);

  return (
    <div className={`${classes["contact"]} ${"container"}`} id="contact">
      <h1>Contact</h1>
      {!theState && (
        <button
          onClick={() => {
            setState(!theState);
          }}
        >
          Show Form
        </button>
      )}
      {theState && <Form />}
    </div>
  );
};

export default Contact;
