import classes from "./Contact.module.css";
import { useRef, useState } from "react";

const Contact = () => {
  const firstNameInputRef = useRef();
  const lastNameInputRef = useRef();
  const emailInputRef = useRef();
  const messageInputRef = useRef();
  const [theState, changeState] = useState(false);

  const submitFormHandler = (event) => {
    event.preventDefault();

    const currentName = firstNameInputRef.current.value;
    const currentLastName = lastNameInputRef.current.value;
    const currentEmail = emailInputRef.current.value;
    const currentMessage = messageInputRef.current.value;

    console.log(currentName);
    console.log(currentLastName);
    console.log(currentEmail);
    console.log(currentMessage);

    firstNameInputRef("");
    lastNameInputRef("");
    emailInputRef("");
    messageInputRef("");
  };

  const Email = () => {
    return (
      <a href="mailto:jogucareers@gmail.com">
        <h3 className={`${classes["email"]} margin-center`}>Email</h3>
      </a>
    );
  };

  const Form = () => {
    return (
      <form onSubmit={submitFormHandler}>
        <div className={classes["control-group"]}>
          <div className={classes["form-control"]}>
            <label htmlFor="name">First Name</label>
            <input id="name" type="text" ref={firstNameInputRef} />
          </div>
          <div className={classes["form-control"]}>
            <label htmlFor="name">Last Name</label>
            <input id="name" type="text" ref={lastNameInputRef} />
          </div>
        </div>
        <div className={classes["form-control"]}>
          <label htmlFor="name">E-Mail Address</label>
          <input id="name" type="text" ref={emailInputRef} />
        </div>
        <div className={classes["form-control"]}>
          <label htmlFor="name">Message</label>
          <textarea id="name" type="note" ref={messageInputRef} rows="8" />
        </div>
        <div className="form-actions">
          <button>Submit</button>
        </div>
      </form>
    );
  };

  return (
    <div className={`${classes["contact"]} ${"container"}`} id="contact">
      <h1>Contact</h1>
      <Email />
      <h3>Or</h3>
      {!theState && (
        <button
          onClick={() => {
            changeState(!theState);
          }}
        >
          Show Form
        </button>
      )}
      {theState && <Form />}
    </div>
  );
};

export default Contact;
